mapper.select:
select 

from b_user_role
where b_user_index = #{bUserIndex,jdbcType=VARCHAR}
  and b_role_index = #{bRoleIndex,jdbcType=VARCHAR}

mapper.delete:
delete from b_user_role
where b_user_index = #{bUserIndex,jdbcType=VARCHAR}
  and b_role_index = #{bRoleIndex,jdbcType=VARCHAR}

mapper.update:
update b_user_role


    u_name = #{uName,jdbcType=VARCHAR},


    r_id = #{rId,jdbcType=VARCHAR},


where b_user_index = #{bUserIndex,jdbcType=VARCHAR}
  and b_role_index = #{bRoleIndex,jdbcType=VARCHAR}

update b_user_role
set u_name = #{uName,jdbcType=VARCHAR},
  r_id = #{rId,jdbcType=VARCHAR}
where b_user_index = #{bUserIndex,jdbcType=VARCHAR}
  and b_role_index = #{bRoleIndex,jdbcType=VARCHAR}
